const fs=require("fs");
let obj={
    check(args){
        return new Promise((resolve,reject)=>{
            fs.readFile(`${__dirname}/mime.json`,(err,buffer)=>{
                if(err)
                {
                    throw err;
                }
                let json=JSON.parse(buffer.toString());
                resolve(json[args]);
            });
        })
    }
}
module.exports=obj;